const initialBoard = () => Array(9).fill(null);

const App = () => {
  const [board, setBoard] = useState(initialBoard());
  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="status mb-4">
        <span className="text-lg font-semibold">Player X Turn</span>
        <button className="ml-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-700">
          Reset Game
        </button>
      </div>
      <div className="grid grid-cols-3 gap-1 w-64">
        {Array(9)
          .fill(null)
          .map((_, index) => (
            <button
              key={index}
              className="w-20 h-20 text-2xl font-bold border border-gray-400 flex items-center justify-center bg-white hover:bg-gray-200"
            >
              {null}
            </button>
          ))}
      </div>
    </div>
  );
};

export default App;
